import React, { Fragment } from "react"
import { Link } from "react-router-dom";
import Moment from 'moment';

const FileItem = (props) => {
    let formatFile = props.file.split('.')[1];
    
    return (
        <Fragment>
            <div className="col-lg-6 pb-3">
                <div className="card rounded-3 h-100">
                    <div className="card-body">
                        <h5 className="card-title">{props.title}</h5>
                        <p className="card-text">{Moment(props.uploadDate).format('dddd - DD MMMM yyyy')}</p>
                        <FileIcon format={formatFile} />
                        <Link to={"/CourseDetails/" + props.id} className="link-danger text-decoration-none">
                            Details
                        </Link>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}
export default FileItem;
