import React, { Fragment, useContext } from "react"
import Moment from 'moment';
import { Download } from "react-bootstrap-icons";
import AuthContext from "../../../store/auth-context";

const Details = props => {
    let details = props.DetailsFile;
    const context = useContext(AuthContext);
    const MyDownload =() =>{
        try {
            const response = fetch(
                'https://localhost:44383/api/download/downloadbymyself?userId='+ context.userId+ '&file='+props.details.file);

            if (response.status === 400) {
                throw new Error('There is no file');
            }
            else if (!response.ok) {
                throw new Error('Something went wrong!!!');
            }
            const data = response.json();

            console.log(data);
        }
        catch (error) {
            setError(error.message);
        }
    }

    return (
        Object.keys(details).length !== 0 ? (
            <Fragment>
                <div className="text-center">
                    <h3>Details</h3>
                    <hr />
                    <h4 className="text-secondary">{props.DetailsFile.title}</h4>
                    <p className="small fw-light">{Moment(props.DetailsFile.uploadDate).format('dddd - DD MMMM yyyy')}</p>

                    <hr />
                    <h5>Main file</h5>
                    <button className="btn btn-success btn-sm shadow" type="button" onClick={MyDownload}>
                        Download <Download />
                    </button>
                    {props.DetailsFile.file}
                </div>

            </Fragment >
        ):
        (
            <Fragment>
                <h3 className="text-danger text-center">Please select a file from the list.</h3>
            </Fragment>
        )    
    )
    
}
export default Details;
