import React, { Fragment, useCallback, useState, useEffect, useContext } from "react"
import UserItem from "./UserItem";

const UserList = (props) => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');
    const context = useContext(AuthContext);
    const userId = context.userId;
    const fileId = props.FileId;
    const StoreInfo = event => {
        let url = 'https://localhost:44383/api/share/';
        let method = '';
        let UserProfile = {};

        UserProfile = {
            userFilesId: fileId,
            userId: userId,
        }
        method = 'POST'


        event.preventDefault();
        setIsLoading(true);
        setMessage('');

        fetch(url,
            {
                method: method,
                body: JSON.stringify(UserProfile),
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        ).then(res => {
            if (res.ok) {
                setMessage('You have created profile successfully.');


                setIsLoading(false);
                props.ChangeStateOfForm();
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'There is an error';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).catch((error) => {
            setIsLoading(false);
            setError(error.message);
        });
    }

    const ShareWithUsers = (id) => {
        console.log(id);
    }
    const RetrieveUsers = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch('https://localhost:44383/api/users');
            if (!response.ok) {
                throw new Error('Something went wrong!!!');
            }
            const data = await response.json();
            const loadUsers = [];
            for (const key in data.value) {

                loadUsers.push(
                    {
                        id: data.value[key].id,
                        userName: data.value[key].userName
                    }
                );
            }
            setUsers(loadUsers);
        }
        catch (error) {
            setError(error.message);
        }
        setIsLoading(false);
    }, []);

    useEffect(() => {
        RetrieveUsers();
    }, [RetrieveUsers])

    let content = <p className='fs-4 text-center'>There is no Users in the list...</p>;
    if (isLoading) {
        content = (
            <div className="d-flex justify-content-center mt-5 mb-5">
                <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>
        );
    }
    if (error) {
        content = <p className='text-danger text-center fs-4'>{error}</p>;
    }
    if (users.length > 0) {
        content = (
            users.map(user =>
                <UserItem
                    id={user.id}
                    key={user.id}
                    userName={user.userName}
                />
            )
        )
    }
    return (
        <Fragment>
            {content}
        </Fragment>
    )
}
export default UserList;
