import React, { Fragment } from "react"
import Moment from 'moment';
import FileIcon from "../Dashboard/Files/FileIcon";

const InboxItem = (props) => {
    const current = new Date();
    console.log(Moment(props.expirationDate).format('DD MMMM yyyy'));
    let expired;
    if(props.expirationDate >= new Date)
    {
        expired = true;
    }
    return (
        <Fragment>
            <div className="col-lg-6 pb-3">
                <div className="card rounded-3 h-100">
                    <div className="card-body">
                        <h5 className="card-title">{props.senderUserName}</h5>
                        <p className="card-text">{Moment(props.expirationDate).format('dddd - DD MMMM yyyy')}</p>
                        {expired && <p>It expired. You can ask from {props.senderUserName} to send it again.</p> }
                        <p>
                            <FileIcon format={props.fileName} />
                        </p>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}
export default InboxItem;
