import React, { Fragment, useState } from "react"
import { useHistory } from "react-router-dom";

const AddMultipleFiles = (props) => {
    const [message, setMessage] = useState('');
    const [error, setError] = useState();
    const [isLoading, setIsLoading] = useState(false);
    const fileId = props.fileId;
    const history = useHistory();

    const saveFile = (e) => {
        setFile(e.target.files[0]);
    }


    const postData = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("UserFileId", fileId)

        fetch("https://localhost:44383/api/userFiles/uploadmultiple",
            {
                method: 'POST',
                body: formData
            }
        ).then(res => {
            if (res.ok) {

                setMessage('Upload file succeeded.');
                setIsLoading(false);
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'Upload file failed';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).catch((error) => {
            setIsLoading(false);
            setMessage(error.message);
        });

    }

    return (
        <Fragment>
            <div className="pt-1">
                <h5 className="text-light">You can add extra files for {props.fileName}</h5>
                <div className="form-group">
                    <label className="form-label" htmlFor="file">File</label>
                    <input type="file" className="form-control" id="file" onChange={saveFile} />
                </div>
                <div className="row">
                    <button className="btn btn-primary float-end rounded-3 shadow" onClick={postData}>Upload</button>
                </div>
            </div>
        </Fragment>
    )
}
export default AddMultipleFiles;
