import React, { Fragment, useState } from "react"
import { useHistory } from "react-router-dom";

const AddMultipleFiles = (props) => {
    const [message, setMessage] = useState('');
    const [error, setError] = useState();
    const [isLoading, setIsLoading] = useState(false);
    const [file, setFile] = useState();
    const fileId = props.fileId;
    const history = useHistory();

    const saveFile = (e) => {
        const files =[];
        console.log(e.target.files[0]);
        for (let index = 0; index < e.target.files.length; index++) {
            files.push(e.target.files[index])
        }
        console.log(file);
    }


    const postData = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("UserFileId", fileId)
        formData.append("UploadFile", file)

        fetch("https://localhost:44383/api/userFiles/uploadmultiple",
            {
                method: 'POST',
                body: formData
            }
        ).then(res => {
            if (res.ok) {

                setMessage('Upload file succeeded.');
                setIsLoading(false);
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'Upload file failed';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).catch((error) => {
            setIsLoading(false);
            setMessage(error.message);
        });

    }

    return (
        <Fragment>
            <div className="row">
                <h5 className="text-secondary">You can add extra files for {props.fileName}</h5>
                <div className="form-group">
                    <label className="form-label" htmlFor="file">File</label>
                    <input type="file" className="form-control" id="file" onChange={saveFile} multiple />
                </div>
            </div>
            <div className="col-md-12">
                <button className="btn btn-primary float-end rounded-3 shadow mt-2 mb-4" onClick={postData}>Upload</button>
            </div>
        </Fragment>
    )
}
export default AddMultipleFiles;
