import { Fragment, useContext, useState } from "react"
import AuthContext from "../../../store/auth-context";

const AddFileForm = () => {
    const context = useContext(AuthContext);
    const [title, setTitle] = useState('');
    const [file, setFile] = useState();
    const [message, setMessage] = useState('');

    const TitleEventHandler = (event) => {
        console.log(event.target.value);
        setTitle(event.target.value);
    }

    const saveFile = (e) => {
        setFile(e.target.files[0]);
    }

    const postData = (e) => {
        e.preventDefault();
        setUserId(props.userId)
        const formData = new FormData();
        formData.append("Title", title)
        formData.append("File", file)
        formData.append("UserId", context.userId)

        fetch("https://localhost:44383/api/userFiles/upload",
            {
                method: 'POST',
                body: formData
            }
        ).then(res => {
            if (res.ok) {

                setMessage('Authentication succeeded.');
                setIsLoading(false);
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'Authentication failed';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).then((data) => {
            
            history.replace('/dashboard');

        }).catch((error) => {
            setIsLoading(false);
            setMessage(error.message);
        });

    }

    return (
        <Fragment>
            <div className="shadow rounded-3 mt-3">
                <div className="p-3">
                    <h3 className="text-secondary">Add file</h3>
                    <p className="small">You can add any type of file then you can add multiple files from details section in your dashboard.</p>
                    <hr />
                    <div className="row">
                        <div className="col-md-6">
                            <label className="form-label" htmlFor="Title">Title</label>
                            <input className="form-control" id="Title" onChange={TitleEventHandler} placeholder="Enter your title" />
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" htmlFor="file">File</label>
                            <input type="file" className="form-control" id="file" onChange={saveFile} />
                        </div>

                    </div>
                    <div className="row">
                        <div className="pt-3">
                            <button className="btn btn-primary shadow rounded-3 float-end">Upload</button>
                        </div>
                    </div>
                </div>
            </div>

        </Fragment>
    )
}
export default AddFileForm;
