import React, { Fragment, useState } from "react"
import { XLg } from "react-bootstrap-icons";
import FileIcon from "./FileIcon";

const SubFileItem = (props) => {
    const [isLoading, setIsLoading] = useState(false);
    const Delete = () => {
        fetch("https://localhost:44383/api/userfiles/deletesubfiles?id=" + props.id + "&userFilesId=" + props.fileId,
            {
                method: 'Delete'
            }
        ).then(res => {
            if (res.ok) {
                props.onRemove(props.id);
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'Delete failed';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).catch((error) => {
            setIsLoading(false);
        });
    }

    return (
        <Fragment>
            <div className="col-lg-6 pb-3">
                <div className="card rounded-3 h-100">
                    <div className="card-body">
                        <h5 className="card-title">{props.fileName}</h5>
                        <p>
                            <FileIcon format={props.fileName} />
                        </p>
                        <button type="button" onClick={Delete} className="border border-1 btn btn-sm rounded-circle mx-1">
                            {isLoading && <div className="d-flex justify-content-center">
                                <div className="spinner-border" role="status">
                                    <span className="visually-hidden">Loading...</span>
                                </div>
                            </div>}
                            {!isLoading && <XLg className="text-danger mb-1" size={15} />}

                        </button>
                        {/* <label className="float-end border border-1 p-1 w-50 text-center shadow rounded-3"><Download className="mb-1"/>{downloadCount} times</label> */}
                    </div>
                </div>
            </div>
        </Fragment>
    )
}
export default SubFileItem;
