import React, { Fragment } from "react"
import { FileEarmarkFill, FileEarmarkPdfFill, FileEarmarkText, FileEarmarkWordFill, FiletypeJpg } from "react-bootstrap-icons";

const FileIcon = (props) => {
    const formatFile = props.format.split('.')[1];
    let content;
    switch (formatFile) {
        case "pdf":
            content = <FileEarmarkPdfFill className="text-danger" />
            break;
        case "docx":
            content = <FileEarmarkWordFill className="text-primary" />
            break;
        case "txt":
            content = <FileEarmarkText className="text-secondary" />
            break;
        case "jpg":
            content = <FiletypeJpg className="text-warning" />
            break;
        case "png":
            content = <FiletypeJpg className="text-danger" />
            break;
        default:
            content = <FileEarmarkFill />
    }
    return (
        <Fragment>
            {content}
        </Fragment>
    )
}
export default FileIcon;
