import React, { Fragment, useContext } from "react"
import AuthContext from "../../../store/auth-context";
import Modal from "./Modal";
import UsersList from './UsersList';


const ConvasUsers = (props) => {
    const context = useContext(AuthContext);
    const userId = context.userId;
    const fileId = props.FileId;
    const StoreInfo = event => {
        let url = 'https://localhost:44383/api/share/';
        let method = '';
        let UserProfile = {};

        UserProfile = {
            userFilesId: fileId,
            about: about,
            phoneNumber: tel,
            twitterAccount: twitter,
            facebookAccount: facebook
        }
        method = 'POST'


        event.preventDefault();
        setIsLoading(true);
        setMessage('');

        fetch(url,
            {
                method: method,
                body: JSON.stringify(UserProfile),
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        ).then(res => {
            if (res.ok) {

                // do something with login 
                // You can redirect or show a string message
                if (props.isEdit) {
                    setMessage('Your profile has been updated successfully.');
                }
                else {
                    setMessage('You have created profile successfully.');
                }

                setIsLoading(false);
                props.ChangeStateOfForm();
                return res.json();
            }
            else {
                return res.json().then((data) => {
                    let errorMessage = 'There is an error';
                    if (data && data.error && data.error.message) {
                        errorMessage = data.error.message;
                    }
                    throw new Error(errorMessage);
                });
            }
        }).catch((error) => {
            setIsLoading(false);
            setError(error.message);
        });
    }

    return (
        <Modal onCloseModal={props.onClose}>
            <UsersList />
        </Modal>
    )
}
export default ConvasUsers;
