import React, { Fragment, useContext, useState } from "react"
import Moment from 'moment';
import { Download } from "react-bootstrap-icons";
import AuthContext from "../../../store/auth-context";

const Details = props => {
    let details = props.DetailsFile;
    const context = useContext(AuthContext);
    const [error,setError] = useState();
    const RetrieveFiles = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch('https://localhost:44383/api/UserFiles?userId=' + context.userId);

            if (response.status === 404) {
                throw new Error('There is no file in your list');
            }
            else if (!response.ok) {
                throw new Error('Something went wrong!!!');
            }
            const data = await response.json();

            const loadFiles = [];
            for (const key in data.value) {

                loadFiles.push(
                    {
                        id: data.value[key].id,
                        key: data.value[key].id,
                        title: data.value[key].title,
                        uploadDate: data.value[key].uploadDate,
                        file: data.value[key].file
                    }
                );
            }
            setFiles(loadFiles);
        }
        catch (error) {
            setError(error.message);

        }
        setIsLoading(false);
    }, [context.userId]);
    const MyDownload =() =>{
       
    }

    return (
        Object.keys(details).length !== 0 ? (
            <Fragment>
                <div className="text-center">
                    <h3>Details</h3>
                    <hr />
                    <h4 className="text-secondary">{props.DetailsFile.title}</h4>
                    <p className="small fw-light">{Moment(props.DetailsFile.uploadDate).format('dddd - DD MMMM yyyy')}</p>

                    <hr />
                    <h5>Main file</h5>
                    <button className="btn btn-success btn-sm shadow" type="button" onClick={MyDownload}>
                        Download <Download />
                    </button>
                    {props.DetailsFile.file}
                </div>

            </Fragment >
        ):
        (
            <Fragment>
                <h3 className="text-danger text-center">Please select a file from the list.</h3>
            </Fragment>
        )    
    )
    
}
export default Details;
